mapper.select:
select id, Name, Password, Email, GmtCreated, GmtModified
from User
where id = #{id,jdbcType=VARCHAR}

select id, Name, Password, Email, GmtCreated, GmtModified
from User
where Name = #{name,jdbcType=VARCHAR}
  and Password = #{pwd,jdbcType=VARCHAR}

select id, Name, Password, Email, GmtCreated, GmtModified
from User
where Name = #{name,jdbcType=VARCHAR}

select id, Name, Password, Email, GmtCreated, GmtModified
from User
where Email = #{email,jdbcType=VARCHAR}
  and Password = #{pwd,jdbcType=VARCHAR}

select id, Name, Password, Email, GmtCreated, GmtModified
from User
where Email = #{email,jdbcType=VARCHAR}
